import React, { useState } from 'react'
import {
    Modal,
    ModalOverlay,
    Button,
    IconButton,
    ModalContent,
    ModalHeader,
    ModalFooter,
    Box,
    ModalBody,
    ModalCloseButton,
    useToast,
    FormControl,
    Input,
    useDisclosure
  } from '@chakra-ui/react'
  import UserBadgeItem from './UserBadgeItem';
import { ViewIcon } from '@chakra-ui/icons'
import { useAuthContext } from '../../hooks/useAuthContext'
import { useChatContext } from '../../hooks/useChatContext'

const UpdateGroupChatModal = ({ fetchAgain, setFetchAgain }) => {
    const [groupChatName, setGroupChatName] = useState('')
    const [search, setSearch] = useState('')
    const [searchResult, setSearchResult] = useState([''])
    const [loading, setLoading] = useState(false)
    const [renameLoading, setRenameLoading] = useState(false)
    const { isOpen, onOpen, onClose } = useDisclosure()
    const { selectedChat, setSelectedChat, } = useChatContext() 
    const {user} = useAuthContext();
    const toast = useToast(); 


    const removeGroupUser = (user) => {

    }

    const handleRename = async() => {
        if (!groupChatName) return
        
        try {
            const params = {
                    "chatId": selectedChat._id,
                    "chatName": groupChatName,
            }
            setRenameLoading(true)
            const response = await fetch(`/api/chat/rename`, {
                method: 'PUT',
                headers: {
                  'Content-type': 'application/json',
                  'Authorization': `Bearer ${user.token}`
                },
                body: JSON.stringify(params)
            })
            setSelectedChat(response)
            setFetchAgain(prev => !prev)
            setRenameLoading(false)
        } catch (error) {
            toast({
                title: 'error occurred',
                description: error.response.data.message,
                status: 'error',
                duration: 5000,
                isClosable: true,
                position: 'bottom'
            })
            setRenameLoading(false)

        }
        setGroupChatName('')
    }
    const handleSearch = () => {

    }
    const handleRemove = (user) => {

    }
   
    return (
        <>
        <IconButton display={{base: 'flex'}} icon={<ViewIcon/>} onClick={onOpen}>Open Modal</IconButton>
  
        <Modal isOpen={isOpen} onClose={onClose} isCentered>
          <ModalOverlay />
          <ModalContent>
            <ModalHeader
            fontSize='35px'
            fontFamily='Work sans'
            display='flex'
            justifyContent='center'
            >
            {selectedChat.chatName}
            </ModalHeader>
            <ModalCloseButton />
            <ModalBody>
            <Box w='100%' display='flex' flexWrap='wrap' pb={3} justifyContent='center'>
            {selectedChat.users.map(u => (
                <UserBadgeItem
          key={`${u.email}-${u._id}-${u.name}`}
          user={u}
          admin={user}
          handleFunction={()=> removeGroupUser(u)}
     />
            ))}
            </Box>
            <FormControl display='flex'>
              <Input
                placeholder='Change Chat Name...'
                mb={3}
                value={groupChatName}
                onChange={(e) => setGroupChatName(e.target.value)}
              />
              <Button
                variant='solid'
                colorScheme='teal'                
                ml={1}
                isLoading={renameLoading}
                onClick={handleRename}
                >                
                Update
              </Button>
            </FormControl>
            <FormControl display='flex'>
              <Input
                placeholder='Add user to group...'
                mb={1}
                onChange={(e) => handleSearch(e.target.value)}
              />
              <Button
                variant='solid'
                colorScheme='teal'                
                ml={1}
                isLoading={renameLoading}
                onClick={handleRename}
                >                
                Add User
              </Button>
            </FormControl>
            </ModalBody>
  
            <ModalFooter>
              <Button colorScheme='red' onClick={()=> handleRemove(user)}>
                Leave Group
              </Button>
              {/* <Button variant='ghost'>Secondary Action</Button> */}
            </ModalFooter>
          </ModalContent>
        </Modal>
      </>
  )
}

export default UpdateGroupChatModal
