import React, {useState} from 'react'
import { useChatContext } from '../../hooks/useChatContext';
import { useAuthContext } from '../../hooks/useAuthContext';
import { Box, FormControl, IconButton, Input, Spinner, Text } from '@chakra-ui/react';
import { ArrowBackIcon } from '@chakra-ui/icons';
import { getSender, getSenderProfile } from '../../config/ChatLogic';
import  ProfileModal from './ProfileModal';
import UpdateGroupChatModal from './UpdateGroupChatModal';

const SingleChat = ({ fetchAgain, setFetchAgain }) => {
    const { selectedChat, setSelectedChat } = useChatContext();
    const { user } = useAuthContext()
    const [messages, setMessages] = useState([])
    const [newMessage, setNewMessage] = useState([])

    const [loading, setLoading] = useState(false)
    //returns other users name as chat title when clicking on a one-to-one 
    // chat
    const getOtherUser = () => selectedChat.users[0].name === user.name ? selectedChat.users[1].name : selectedChat.users[0].name
    const SendMessage = async(e) => {
      console.log(e.key)
      if (e.key === 'Enter' && newMessage) {
        try {

          let param = {
            chatId: selectedChat._id,
            content: newMessage,
          };

            const response = await fetch(`/api/message`, {
              method: 'POST',
              headers: {
                'Content-type': 'application/json',
                'Authorization': `Bearer ${user.token}`,
              },
              body: JSON.stringify(param)
            }) 
            const json = await response.json(); 


        } catch (error) {
          console.log(error)
        }
        }
    }
  

        const  messageHandler    = async(e) => {
          setNewMessage(e.target.value);
         
          // typing indicator 

    }
    return (
    <>
      {selectedChat ? (
        <>

        <Text
        fontSize={{ base: "28px", md: "30px" }}
        pb={3}
        px={2}
        w="100%"
        fontFamily="Work sans"
        display="flex"
        justifyContent={{ base: "space-between" }}
        alignItems="center"
        >
        {/* left arrow to show MyChats component */}
        <IconButton 
        display={{base: 'flex', md: 'none'}}
        icon={<ArrowBackIcon/>}
        onClick={() => setSelectedChat('')}
        />

        {(!selectedChat.isGroupChat ? (
                <>
                  {getSender(user, selectedChat.users)}
                  <ProfileModal
                    user={getSenderProfile(user, selectedChat.users)}
                  />
                </>
              ) : (
                <>
                  {selectedChat.chatName.toUpperCase()}
                  <UpdateGroupChatModal
                    // fetchMessages={fetchMessages}
                    fetchAgain={fetchAgain}
                    setFetchAgain={setFetchAgain}
                  />
                </>
              ))}
          </Text>
          <Box
          fontSize={{ base: "28px", md: "30px" }}
          p={3}
          w="100%"
          h='100%'
          borderRadius='lg'
          bg='#E8E8E8'
          display="flex"
          flexDir='column'
          justifyContent="flex-end"
          >
                        {loading ? (<Spinner 
                        size='x1'  
                        w={20}
                        h={20}
                        alignSelf='center'
                        margin='auto'
                        />  )
                        
                         : (
                          <div>messages</div>
                          )}
          <FormControl onKeyDown={SendMessage}>
              <Input
                variant='filled'
                bg='#E0E0E0'
                placeholder='Enter a message'
                onChange={messageHandler}
                value={newMessage}

              />

           
          </FormControl>
          </Box>
          </>
      ) : (
        <Box display='flex' alignItems='center' justifyContent='center' h='100%'>
            <Text fontSize='3x1' pb={3} fontFamily='Work sans'>
              Click on a User to Start Chatting

            </Text>
        </Box>

      )}
    </>
  )
}

export default SingleChat
